package arithmaticOperatorsPractice;

public class BitwiseSymbols {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;
		int b=10;
		int c=a&b;
		int d=a|b;
		int e=a^b;
		System.out.println("Initial value of a:"+a);
		System.out.println("Initial value of b:"+b);
		System.out.println("Bitwise & opration:"+c);
		System.out.println("Bitwise | opration:"+d);
		System.out.println("Bitwise ^ opration:"+e);
		

	}

}
